import React, { useCallback } from 'react'
import { Card as ACard, Carousel as ACarousel, Image as AImage, Button as AButton, Alert as AAlert } from 'antd';
import { useNodeRef } from '../hooks/useNodeRef';
const Grid = ACard.Grid as any

class Wrapper extends React.Component {
  render() {
    const { children } = this.props
    return (
      <React.Fragment>
        { children }
      </React.Fragment>
    )
  }
}

export const CardGrid = ({ children, ...props }) => {
  const refFn = useNodeRef() as any
  // const refFn = useCallback((el) => {
  //   console.log(el)
  // }, [])
  return (
    <Wrapper ref={refFn}>
      <Grid {...props}>
      { children }
      </Grid>
    </Wrapper>
  )
}

export const Card = ({ children, ...props}) => {
  const refFn = useNodeRef()
  return (
    <Wrapper ref={refFn}>
      <ACard {...props}>
        { children }
      </ACard>
    </Wrapper>
  )
}

export const Carousel = () => {
  // const refFn = useCallback((el) => {
  //   console.log(el)
  // }, []) as any
  const refFn = useNodeRef()
  const contentStyle = {
    height: '160px',
    color: '#fff',
    lineHeight: '160px',
    textAlign: 'center',
    background: '#364d79',
  } as any

  return (
    <ACarousel autoplay ref={refFn} style={{ width: 500, display: 'inline-block' }}>
      <div>
        <h3 style={contentStyle}>1</h3>
      </div>
      <div>
        <h3 style={contentStyle}>2</h3>
      </div>
      <div>
        <h3 style={contentStyle}>3</h3>
      </div>
      <div>
        <h3 style={contentStyle}>4</h3>
      </div>
  </ACarousel>
  )
}

export const Image = ({ ...props }) => {
  const refFn = useNodeRef()
  return (
    <Wrapper ref={refFn}>
      <AImage
        fallback="data:image/png;base64,"
        {...props}
      />
    </Wrapper>

  )
}

export const Container = ({children}) => {
  const refFn = useNodeRef()
  return (
    <div ref={refFn}>
      { children}
    </div>
  )
}

export const Button = ({ children, ...props }) => {
  const refFn = useNodeRef()

  return (
    <AButton {...props} ref={refFn}>
      { children }
    </AButton>
  )
}

export const Alert = ({ children = null, ...props }) => {
  const refFn = useNodeRef() as any

  return (
    <Wrapper ref={refFn}>
      <AAlert {...props as any }>
        { children }
      </AAlert>
    </Wrapper>
  )
}